from rest_framework.test import APITestCase
from mongoengine import connect

from django.conf import settings


class BaseTests(APITestCase):

    data = {

        "country": "testcountry",
        "languages": {
                "spanish": {
                    "word": {
                        "images": [{

                            "file": ""
                        }],
                        "audio": {

                            "files": {
                                "male": {
                                    "region1": {
                                        "file": None
                                    },
                                    "default": {
                                        "file": None
                                    }
                                },
                                "female": {
                                    "region1": {
                                        "file": None
                                    },
                                    "default": {
                                        "file": None
                                    }
                                }
                            }
                        }
                    }
                }
            }
    }

    def __init__(self, methodName='runtest'):
        self.db = connect(settings.MONGO_DATABASE_NAME)
        super(BaseTests, self).__init__(methodName)

    def _post_teardown(self):
        super(BaseTests, self)._post_teardown()
        self.db.drop_database(settings.MONGO_DATABASE_NAME)

